components/products/ratings:
{{!

  @param schema_org - whether or not to render schema tags. Should be false for everything but the product page.
  @param show_reviews - whether or not to render review count
  @param total_reviews - amount of total reviews
  @param rating - rating count
}}

<span class="rating-stars-readonly" aria-hidden="true">
  {{> components/common/rating rating=rating}}
</span>

{{#if show_reviews}}
  {{#if total_reviews}}
    <span {{#if schema_org}}itemprop="aggregateRating" itemscope itemtype="http://schema.org/AggregateRating"{{/if}}>
      <span class="sr-only ratings-total-score">
        <span {{#if schema_org}}itemprop="ratingValue"{{/if}}>{{rating}}</span>/<span>5</span>
      </span>
      <span class="rating-total-votes">(<span {{#if schema_org}}itemprop="reviewCount"{{/if}}>{{total_reviews}}</span> {{lang 'product.reviews.number_reviews' total=total_reviews}})</span>
    </span>
  {{/if}}
{{/if}}
